function clock(){let t=new Date;var e=t.amPm();let n=t.getHours(),r=t.getMinutes();var o=t.getFullYear();let a=t.getMonth()+1,i=t.getDate();n=(n%=12)||12;o=o+"-"+("00"+a.toString()).slice(-2)+"-"+("00"+i.toString()).slice(-2);document.getElementById("ampm").innerHTML=e,document.getElementById("time").innerHTML=n+":"+r.toString().padStart(2,"0"),document.getElementById("date").innerHTML=o}Date.prototype.amPm=function(){return this.getHours()<12?"오전":"오후"},setInterval(clock,1e3);