$(function(){Date.prototype.amPm=function(){return this.getHours()<12?"오전":"오후"};let t=new Date;var e=t.amPm();let n=t.getHours(),r=t.getMinutes();var i=t.getFullYear();let m=t.getMonth()+1,o=t.getDate();n=(n%=12)||12;i=i+"-"+("00"+m.toString()).slice(-2)+"-"+("00"+o.toString()).slice(-2);document.getElementById("ampm").innerHTML=e,document.getElementById("time").innerHTML=n+":"+r.toString().padStart(2,"0"),document.getElementById("date").innerHTML=i});